import React, { useState, useEffect } from 'react';
import editAvatarButton from '../../images/avatar-edit-icon.svg';
import api from '../../utils/Api'; // Импортируем модуль API

function Main({
  onEditProfile,
  onAddPlace,
  onEditAvatar,
}) {
  const [userName, setUserName] = useState(''); // стейт для userName
  const [userDescription, setUserDescription] = useState(''); // стейт для userDescription
  const [userAvatar, setUserAvatar] = useState(''); // стейт для userAvatar
  const [cards, setCards] = useState([]); 

  useEffect(() => {
    // Функция для получения пользовательских данных из API
    const fetchUserData = async () => {
      try {
        const userData = await api.getUserInfo();
        setUserName(userData.name); // Стейт имя
        setUserDescription(userData.about); // Стейт описание
        setUserAvatar(userData.avatar); // Стейт url
      } catch (error) {
        console.error('Ошибка при загрузке пользовательских данных:', error);
      }
    };

    const fetchCards = async () => {
      try {
        const cardsData = await api.getInitialCards();
        setCards(cardsData); // Обновляем состояние карточек
      } catch (error) {
        console.error('Ошибка при загрузке карточек:', error);
      }
    };

    fetchUserData(); // Функция при монтировании userData
    fetchCards(); // Функция при монтировании Cards
  }, []);

  return (
    <main>
      <section className="profile">
      <div className="profile__avatar">
        <button className="profile__picture-edit-button" type="button" aria-label="редактировать" onClick={onEditAvatar}>
          <img src={editAvatarButton} alt="редактировать"/>
        </button>
        <img className="profile__picture" src={userAvatar} alt="аватар"/>
      </div>

        <div className="profile__info">
          <h1 className="profile__heading">{userName}</h1>
          <button className="profile__edit-button" type="button" aria-label="кнопка редактировать" onClick={onEditProfile}></button>
          <p className="profile__caption">{userDescription}</p>
        </div>
        <button className="profile__add-button" type="button" aria-label="кнопка добавить" onClick={onAddPlace} ></button>
      </section>

      <section className="elements">
        {cards.map((card) => (
          <div className="element" key={card._id}>
            <img className="element__image" src={card.link} alt={card.name} />
            <div className="element__info">
              <h2 className="element__title">{card.name}</h2>
              <div className="element__like-container">
                <button className="element__like-button"></button>
                <p className="element__like-count">{card.likes.length}</p>
              </div>
            </div>
            <button className="element__delete-button"></button>
          </div>
        ))}
      </section>
    </main>
  );
}

export default Main;
