import React, { useState, useEffect } from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm';

function PopupProfile(props) {


  const [name, setName] = useState('');
  const [description, setDescription] = useState('');

  useEffect( () => { setName(userItem.name); setDescription(userItem.about) }, [ props.isOpen ]);
  function handleSubmit (event) {
    event.preventDefault();
    props.onUpdateUser( { name: name, about: description } );
  }
  function handleName (event) { setName(event.target.value) }
  function handleDescription (event) { setDescription(event.target.value) }

  return (
    <PopupWithForm
      isOpen = { props.isOpen }
      onClose = { props.onClose }
      //onSubmit = { handleSubmit }
      title = {'Редактировать профиль'}
      name = {'profile'}
      buttonName = {'Сохранить'}
      >
      <div className="popup__input-container">
        <input
          className="popup__input-item"
          type="text"
          placeholder="Имя"
          name="popupInputProfileName"
          required
          minLength="2"
          maxLength="40"
        />
        <span className="popup__input-error" id="popupInputProfileName-error"></span>
        <input
          className="popup__input-item"
          type="text"
          placeholder="О себе"
          name="popupInputProfileAbout"
          required
          minLength="2"
          maxLength="200"
        />
        <span className="popup__input-error" id="popupInputProfileAbout-error"></span>
      </div>

    </PopupWithForm>
  );
}

export default PopupProfile;
