import React from 'react';

function Card({ card, onCardClick }) {
  function handleClick() {
    onCardClick(card);
  }

  return (
    <div class="element">
         <img class="element__picture" src={card.image} alt={card.title} onClick={handleClick} />
         <button class="element__delete-button" type="button" aria-label="удалить"></button>
         <div class="element__content-wrapper">
             <h2 class="element__heading">{card.title}</h2>
             <div class="element__like-wrapper">
                 <button class="element__like-button" type="button" aria-label="лайк"></button>
                 <p class="element__like-counter"></p>
             </div>
         </div>
     </div>
   )
}

export default Card;
