import React, { useRef } from 'react'
import PopupWithForm from '../PopupWithForm/PopupWithForm';

function PopupAvatar(props) {
  //Реф аватара
  const avatarRef = useRef('');

  // Очистка формы
  useEffect( () => { 
    avatarRef.current.value = '' }, [ props.isOpen ]);

  function handleSubmit(e) {
    e.preventDefault();
    props.onUpdateAvatar({ avatar: avatarRef.current.value });
  }

  return (
    < PopupWithForm
    isOpen = { props.isOpen }
    onClose = { props.onClose }
    onSubmit = { handleSubmit }
    id = 'avatar-popup'
    title = 'Обновить аватар'
    type = 'user-avatar' >

        <form className="popup__form" name="formAvatar" noValidate>
          <fieldset className="popup__input-container">
            <input
              className="popup__input-item"
              type="url"
              placeholder="Аватар"
              name="popupInputAvatarLink"
              required
              id="avatar-input" 
              ref={ avatarRef }
            />
            <span className="popup__input-error" id="popupInputAvatarLink-error"></span>
          </fieldset>

        </form>
        < /PopupWithForm>

  );
}

export default PopupAvatar
