import React from 'react';

function Card(props) {

  function handleCardClick () { 
    props.onCardClick(props.card) }

  return (
    <div className="element">
         <img className="element__picture" src={props.card.image} alt={props.card.title} onClick={handleCardClick} />
         <button className="element__delete-button" type="button" aria-label="удалить"></button>
         <div className="element__content-wrapper">
             <h2 className="element__heading">{props.card.title}</h2>
             <div className="element__like-wrapper">
                 <button className="element__like-button" type="button" aria-label="лайк"></button>
                 <p className="element__like-counter">{props.card.likes.length}</p>
             </div>
         </div>
     </div>
)
}

export default Card;
