import React from 'react';

function PopupWithForm(props) {
  return (
    <div className={`popup popup_type_${props.name} ${props.isOpen && "popup_state_show"}`}>${ props.isOpen ? 'popup_opened' : '' }` } id={ props.id }
        <div className="popup__container popup__container_type_form">
          <button type="button" className="popup__close-button" onClick={props.onClose}></button>
          <h2 className="popup__heading">{props.title}</h2>
          <form name={props.name} className="popup__form" onSubmit={props.onSubmit}>
            {props.children}
            <button type="submit" className="popup__submit-button">{props.buttonName}</button>
          </form>
        </div>
      </div>
  );
}

export default PopupWithForm;
