import React from 'react';

function Main() {

    // Обработчик открытия попапа для обновления аватара
    const handleEditAvatarClick = () => {
      const avatarPopup = document.querySelector('.popup_type_avatar');
      avatarPopup.classList.add('popup_state_show');
    };

    // Обработчик открытия попапа для редактирования профиля
    const handleEditProfileClick = () => {
      const profilePopup = document.querySelector('.popup_type_profile');
      profilePopup.classList.add('popup_state_show');
    };

    // Обработчик открытия попапа для добавления нового места
    const handleAddPlaceClick = () => {
      const placePopup = document.querySelector('.popup_type_place');
      placePopup.classList.add('popup_state_show');
    };

  return (
    <main>
        <section className="profile">
          <div className="profile__avatar">
            <button className="profile__picture-edit-button" type="button" aria-label="редактировать" onClick={handleEditAvatarClick}>
              <img src="<%=require('./images/avatar-edit-icon.svg')%>" alt="редактировать"/>
            </button>
            <img className="profile__picture" src="#" alt="аватар"/>
          </div>

          <div className="profile__info">
            <h1 className="profile__heading"></h1>
            <button className="profile__edit-button" type="button" aria-label="кнопка редактировать" onClick={handleAddPlaceClick}></button>
            <p className="profile__caption"></p>
          </div>
          <button className="profile__add-button" type="button" aria-label="кнопка добавить" onClick={handleEditProfileClick} ></button>
        </section>

        <section className="elements">
        </section>
      </main>
  );
}

export default Main
