import React from 'react';

function PopupImage(card, props) {
  return (
    <div className={`popup popup_type_image ${ props.isOpen ? 'popup_opened' : '' } id={ props.id }`}> 
      <div className="popup__container  popup__container_type_image">
        <img className="popup__image" src={card ? card.link : ""} alt={card ? card.name : ""} />
        <p className="popup__caption">{card ? card.name : ""}</p>
        <button className="popup__close-button" type="button" aria-label="кнопка закрыть" onClick={props.onClose}></button>
      </div>
    </div>
  );
}

export default PopupImage;
