import React from 'react';

function PopupImage({ card, onClose }) {
  if (!card) {
    // Если card равен null или undefined, не отображаем попап
    return null;
  }

  return (
    <div className={`popup popup_type_image ${card ? 'popup_state_show' : ''}`}>
      <div className="popup__container  popup__container_type_image">
        <img src={card.link} className="popup__image" alt={card.name} />
        <p className="popup__caption">{card.name}</p>
        <button className="popup__close-button" type="button" aria-label="кнопка закрыть" onClick={onClose}></button>
      </div>
    </div>
  );
}

export default PopupImage;
