import React, { useState } from 'react';
import Header from './components/header/Header';
import Main from './components/main/Main';
import Footer from './components/footer/Footer';
import PopupWithForm from './components/PopupWithForm/PopupWithForm';
import PopupAvatar from './components/PopupAvatar/PopupAvatar';
import PopupProfile from './components/PopupProfile/PopupProfile';
//import PopupPlace from './components/PopupPlace/PopupPlace';
import PopupImage from './components/PopupImage/PopupImage';
import PopupConfirmation from './components/PopupConfirmation/PopupConfirmation';

function App() {

  // Стейты попапов
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false); 
  const [isAddPlacePopupOpen, setIsEditPlacePopupOpen] = useState(false); 

  // Обработчик открытия попапа аватара
  function handleEditAvatarClick () { 
    setIsEditAvatarPopupOpen(true) 
  }
  // Обработчик открытия попапа профиля
  function handleEditProfileClick () { 
    setIsEditProfilePopupOpen(true) 
  }
  // Обработчик открытия попапа добавления карточки
  function handleAddPlaceClick () {
     setIsEditPlacePopupOpen(true) 
    }

  function closeAllPopups() {
    setIsEditPlacePopupOpen(false);
      setIsEditAvatarPopupOpen(false);
      setIsEditProfilePopupOpen(false);
    }

  return (
    <div className="root">
      <PopupWithForm />
      <PopupAvatar
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      />
      <PopupProfile 
      isOpen={isEditProfilePopupOpen}
      onClose={closeAllPopups}
      />
      <PopupPlace 
      isOpen={isAddPlacePopupOpen}
      onClose={closeAllPopups}
      />

      <PopupImage />
      <PopupConfirmation />
      <Header />
      <Main 
        onEditAvatar = { handleEditAvatarClick }
        onEditProfile = { handleEditProfileClick }
        onAddPlace = { handleAddPlaceClick }

      />
      <Footer />

    </div>
  );
}

export default App;
