import React, { useState, useEffect} from 'react';
import Header from './components/header/Header';
import Main from './components/main/Main';
import Footer from './components/footer/Footer';
import PopupWithForm from './components/PopupWithForm/PopupWithForm';
import EditAvatarPopup from './components/EditAvatarPopup/EditAvatarPopup';
import EditProfilePopup from './components/EditProfilePopup/EditProfilePopup';
import AddPlacePopup from './components/AddPlacePopup/AddPlacePopup';
import ConfirmPopup from './components/ConfirmPopup/ConfirmPopup';
import ImagePopup from './components/ImagePopup/ImagePopup';
import api from './utils/Api';
import CurrentUserContext from './contexts/CurrentUserContext';

function App() {

  const [currentUser, setCurrentUser] = useState({});

  useEffect(() => {
    api.getUserInfo()
      .then(res => {
        setCurrentUser(res)
      })
      .catch(err => console.log(err));
  }, [])

  // Стейты попапов
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false); 
  const [isAddPlacePopupOpen, setIsEditPlacePopupOpen] = useState(false); 

  const [selectedCard, setSelectedCard] = useState(null);

  const [userName, setUserName] = useState(''); // стейт для userName
  const [userDescription, setUserDescription] = useState(''); // стейт для userDescription
  const [userAvatar, setUserAvatar] = useState(''); // стейт для userAvatar
  const [cards, setCards] = useState([]); 

  useEffect(() => {
    const fetchUserData = () => {
      return api.getUserInfo()
        .then(userData => {
          setUserName(userData.name); // Обновляем стейт имени
          setUserDescription(userData.about); // Обновляем стейт описания
          setUserAvatar(userData.avatar); // Обновляем стейт URL аватара
        })
        .catch(error => {
          console.error('Ошибка при загрузке пользовательских данных:', error);
        });
    };

    const fetchCards = () => {
      return api.getInitialCards()
        .then(cardsData => {
          setCards(cardsData); // Обновляем стейт карточек
        })
        .catch(error => {
          console.error('Ошибка при загрузке карточек:', error);
        });
    };

    Promise.all([fetchUserData(), fetchCards()])

  }, []);


  function handleCardClick(props) {
    setSelectedCard(props);
  }


  // Обработчик открытия попапа аватара
  function handleEditAvatarClick () { 
    setIsEditAvatarPopupOpen(true) 
  }
  // Обработчик открытия попапа профиля
  function handleEditProfileClick () { 
    setIsEditProfilePopupOpen(true) 
  }
  // Обработчик открытия попапа добавления карточки
  function handleAddPlaceClick () {
     setIsEditPlacePopupOpen(true) 
    }

  function closeAllPopups() {
      setIsEditPlacePopupOpen(false);
      setIsEditAvatarPopupOpen(false);
      setIsEditProfilePopupOpen(false);
      setIsConfirmPopupOpen(false);
      setSelectedCard(null);
    }

    function handleCardLike(card) {
      // Снова проверяем, есть ли уже лайк на этой карточке
      const isLiked = card.likes.some(i => i._id === currentUser._id);

      // Отправляем запрос в API и получаем статус лайка на карточке
      api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    })
        .catch(err => console.log(err));
    }

    // Функция удаления карточки
    function handleCardDelete(card) {
      api.removeCard(card._id)
        .then(() => {
          setCards(cards.filter(item => item._id !== card._id));
        })
        .catch(err => console.log(err));
    }

    function handleUpdateUser(user) {
      const userData = {
        name: user.name,
        about: user.about
      };

      api.editUserInfo(userData)
        .then(res => {
          setCurrentUser(res);
          closeAllPopups();
        })
        .catch(err => console.log(err));
    }

    function handleUpdateAvatar(user) {
      api.setAvatar({ avatar: user.avatar })
        .then(res => {
          setCurrentUser(res);
          closeAllPopups();
        })
        .catch(err => console.log(err))
    }

    function handleAddPlaceSubmit(card) {
      const cardData = {
        name: card.name,
        link: card.link,
        owner: currentUser._id
      };

      api.addCard(cardData)
        .then(newCard => {
          setCards([newCard, ...cards]);
          closeAllPopups();
        })
        .catch(err => console.log(err))
    }

    function handleCardDeleteRequest(card) {
      setCardForDelete(card);
      setIsConfirmPopupOpen(true);
    }


  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="root">

        <PopupWithForm />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />
        <EditProfilePopup 
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />
        <AddPlacePopup 
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddPlaceSubmit}
        />

        <ConfirmPopup
          isOpen={isConfirmPopupOpen}
          onClose={closeAllPopups}
          onSubmit={handleCardDelete}
        />

        <ImagePopup
          onClose={closeAllPopups}
          card={selectedCard}
        />

        <Header />
        <Main 
          userAvatar={userAvatar}
          userName={userName}
          userDescription={userDescription}
          onEditAvatar = { handleEditAvatarClick }
          onEditProfile = { handleEditProfileClick }
          onAddPlace = { handleAddPlaceClick }
          cards={cards}
          onCardClick={handleCardClick}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
        />
        <Footer />

      </div>
    </CurrentUserContext.Provider> 

  );
}

export default App;
