import React, { useState, useEffect} from 'react';
import Header from './components/header/Header';
import Main from './components/main/Main';
import Footer from './components/footer/Footer';
import PopupWithForm from './components/PopupWithForm/PopupWithForm';
import PopupAvatar from './components/PopupAvatar/PopupAvatar';
import PopupProfile from './components/PopupProfile/PopupProfile';
import PopupPlace from './components/PopupPlace/PopupPlace';
import PopupImage from './components/PopupImage/PopupImage';
import api from './utils/Api';

function App() {

  // Стейты попапов
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false); 
  const [isAddPlacePopupOpen, setIsEditPlacePopupOpen] = useState(false); 
  const [isImageOpen, setIsImageOpen] = useState(false);

  const [userName, setUserName] = useState(''); // стейт для userName
  const [userDescription, setUserDescription] = useState(''); // стейт для userDescription
  const [userAvatar, setUserAvatar] = useState(''); // стейт для userAvatar
  const [selectedCard, setSelectedCard] = useState(null);
  const [cards, setCards] = useState([]); 


  function handleCardClick (card) {
    setIsImageOpen(true);
    setSelectedCard({
      ...selectedCard,
      name: card.name,
      link: card.link
    })
  }

  useEffect(() => {
    const fetchUserData = () => {
      return api.getUserInfo()
        .then(userData => {
          setUserName(userData.name); // Обновляем стейт имени
          setUserDescription(userData.about); // Обновляем стейт описания
          setUserAvatar(userData.avatar); // Обновляем стейт URL аватара
        })
        .catch(error => {
          console.error('Ошибка при загрузке пользовательских данных:', error);
        });
    };

    const fetchCards = () => {
      return api.getInitialCards()
        .then(cardsData => {
          setCards(cardsData); // Обновляем стейт карточек
        })
        .catch(error => {
          console.error('Ошибка при загрузке карточек:', error);
        });
    };

    Promise.all([fetchUserData(), fetchCards()])

  }, []);

  function handleCardClick(props) {
    setSelectedCard(props);
  }


  // Обработчик открытия попапа аватара
  function handleEditAvatarClick () { 
    setIsEditAvatarPopupOpen(true) 
  }
  // Обработчик открытия попапа профиля
  function handleEditProfileClick () { 
    setIsEditProfilePopupOpen(true) 
  }
  // Обработчик открытия попапа добавления карточки
  function handleAddPlaceClick () {
     setIsEditPlacePopupOpen(true) 
    }

  function closeAllPopups() {
    setIsEditPlacePopupOpen(false);
      setIsEditAvatarPopupOpen(false);
      setIsEditProfilePopupOpen(false);
      setIsImageOpen(false);
      setSelectedCard(null);
    }

  return (
    <div className="root">
      <PopupWithForm />
      <PopupAvatar
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      />
      <PopupProfile 
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      />
      <PopupPlace 
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      />

      <PopupWithForm
        onClose={closeAllPopups}
        name="confirm-delete"
        title="Вы уверены?"
        buttonName="Да"
      />

      <PopupImage 
        isOpen = { isImageOpen }
        card={selectedCard}
        onClose={closeAllPopups}
      />

      <Header />
      <Main 
        userAvatar={userAvatar}
        userName={userName}
        userDescription={userDescription}
        onEditAvatar = { handleEditAvatarClick }
        onEditProfile = { handleEditProfileClick }
        onAddPlace = { handleAddPlaceClick }
        cards={cards}
        onCardClick={handleCardClick}

      />
      <Footer />

    </div>
  );
}

export default App;
