import React, { useState } from 'react';
import Header from './components/header/Header';
import Main from './components/main/Main';
import Footer from './components/footer/Footer';
import PopupWithForm from './components/PopupWithForm/PopupWithForm';
import PopupAvatar from './components/PopupAvatar/PopupAvatar';
//import PopupProfile from './components/PopupProfile/PopupProfile';
//import PopupPlace from './components/PopupPlace/PopupPlace';
import PopupImage from './components/PopupImage/PopupImage';
import PopupConfirmation from './components/PopupConfirmation/PopupConfirmation';

function App() {

  // Стейты попапов
  const [isEditAvatarPopupOpen, setIsAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsProfilePopupOpen] = useState(false); 
  const [isAddPlacePopupOpen, setIsPlacePopupOpen] = useState(false); 

  // Обработчик открытия попапа аватара
  function handleEditAvatarClick () { 
    setIsAvatarPopupOpen(true) 
  }
  // Обработчик открытия попапа профиля
  function handleEditProfileClick () { 
    setIsProfilePopupOpen(true) 
  }
  // Обработчик открытия попапа добавления карточки
  function handleAddPlaceClick () {
     setIsPlacePopupOpen(true) 
    }

  function closeAllPopups() {
    setIsPlacePopupOpen(false);
      setIsAvatarPopupOpen(false);
      setIsProfilePopupOpen(false);
    }


  return (
    <div className="root">
      <PopupWithForm />
      <PopupAvatar />

      <PopupImage />
      <PopupConfirmation />
      <Header />
      <Main 
        isEditAvatarPopupOpen={handleEditAvatarClick}
        isEditProfilePopupOpen={handleEditProfileClick}
        isAddPlacePopupOpen={handleAddPlaceClick}

      />
      <Footer />

    </div>
  );
}

export default App;

